// Miscellaneous JS

(function($) {


	function accordionInit() {
		var menuToggle = $('.acc-toggle');
		var menuList = $('.acc-content').parent();
		menuToggle.on('click', function(e) {
			e.preventDefault();
			var next = $(this).next();
			if (next.is(':hidden')) {
				menuList.find('.acc-content:first').slideUp();
				menuToggle.removeClass('active');
				$(this).addClass('active');
				next.slideDown(function() {
					menuList.find('.acc-content:first').removeClass('show');
				});
			} else {
				$(this).removeClass('active');
				next.slideUp(function() {
					menuList.find('.acc-content:first').removeClass('show');
				});
			}
		});
	}


	function dataTableInit() {
		$('.table-wrapper').each(function() {
			
			var $tr = $(this).find('tbody tr');
			var $td = $tr.children();
			var $trWidth = $tr.outerWidth(true);
			var $tableHeader = $(this).find('.table-header table');
			var $tableHeaderTh = $tableHeader.find('thead th');

			// $tableHeader.css({
			// 	'width': $trWidth
			// });

			
			for (var i = 0; i < $td.length; i++) {
				var $tdWidth = $td.eq(i).outerWidth(true);
				$tableHeaderTh.eq(i).css({
					'width': $tdWidth,
					'min-width': $tdWidth
				});
			}

			//$tableBody.width($trWidth);

			$('.table-scroll').on('scroll', function() {
				var scrollLeft = -$(this).scrollLeft();
				$('.table-header').css({
					'transform': 'translateX('+ scrollLeft +'px)'
				})
			});


		});
	}


	function datePickerInit() {
        $('.timepicker').each(function() {
        	$(this).datetimepicker({
				format: 'MM/DD/YYYY'
	        });
        });
	}


	$(window).on('load', function() {
		dataTableInit();
	});

	$(function() {
		FastClick.attach(document.body);

		accordionInit();
		dataTableInit();
		datePickerInit();

		$('[data-toggle="tooltip"]').tooltip();



	});
		
})(jQuery);